function processSelectedFile(e, t) {
    var result = e.split('All Uploads');
    var path=$("." + t).attr('data-path');

    var img_url=path+'FileUploader'+result[1];
    img_url=img_url.replace('\\', "/");
    var input_value='FileUploader'+result[1];
    input_value=input_value.replace('\\', "/");
    $('html').removeClass('scroll-bar-hide')
    $("#" + t).val(input_value)
    $('.'+ t).find('.img-after-upload').remove();
    $("."+t).find('#remove-icon').html('');
    $('#popup_selector').append('<div class="img-after-upload"><img class="card-img-top img-responsive " src="'+img_url+'" alt="Card image cap"></div>')
    $("."+ t).append('<div class="img-after-upload"><img class="card-img-top img-responsive " src="'+img_url+'" alt="Card image cap"></div>')
    $("."+t).find('#remove-icon').append('<span onclick="remove_image(this)" class="remove-img-after-upload"><i class="mdi mdi-close-circle"></i>حذف</span>');

    function remove_image(item) {
        $(item).parents('.form-group').find('input').val('');
        $(item).parents('.popup_selector').find('.img-after-upload').remove();
        $(item).parents('.popup_selector').find('#remove-icon').html('');

    }

}

$(document).on("click", ".popup_selector", function (e) {
    e.preventDefault();
    var t = $(this).attr("data-inputid");
    var n = "/admin/FileManager/popup/";
    var r = n + t;
    $.colorbox({href: r, fastIframe: true, iframe: true, width: "70%", height: "50%"});

})


